package mobile

import (
	"github.com/textileio/textile-go/pb"
)

// QueryEvent represents events generated by a search query
type QueryEvent struct {
	Type QueryEventType  `json:"type"`
	Data *pb.QueryResult `json:"data"`
}

// QueryEventType is used to indicate a type of query event
type QueryEventType string

const (
	// QueryData a match was found
	QueryData QueryEventType = "data"
	// QueryDone query is complete or was canceled
	QueryDone = "done"
)
